$(function(){'use strict';$('#start-slideshow').button().click(function(){var options=$(this).data(),modal=$(options.target),data=modal.data('modal');if(data){$.extend(data.options,options);}else{options=$.extend(modal.data(),options);}
modal.find('.modal-slideshow').find('i').removeClass('icon-play').addClass('icon-pause');modal.modal(options);});$('#toggle-fullscreen').button().click(function(){var button=$(this),root=document.documentElement;if(!button.hasClass('active')){$('#modal-gallery').addClass('modal-fullscreen');if(root.webkitRequestFullScreen){root.webkitRequestFullScreen(window.Element.ALLOW_KEYBOARD_INPUT);}else if(root.mozRequestFullScreen){root.mozRequestFullScreen();}}else{$('#modal-gallery').removeClass('modal-fullscreen');(document.webkitCancelFullScreen||document.mozCancelFullScreen||$.noop).apply(document);}});$.getJSON("imglist.min.js",function(photos){var gallery=$('#gallery');$(photos).each(function(){var url_s='img_s/'+this.name;var url_b='img_b/'+this.name;$('<a rel="gallery"/>').append($('<img height=\'75\'>').prop('src',url_s).prop('width',this.width)).prop('href',url_b).prop('title',this.title).appendTo(gallery);});});});